import{_ as e,c as d,o,aj as r}from"./chunks/framework.D1pnFkAx.js";const p=JSON.parse('{"title":"Effects","description":"","frontmatter":{},"headers":[],"relativePath":"styling/effects.md","filePath":"styling/effects.md"}'),a={name:"styling/effects.md"};function c(s,t,i,l,n,h){return o(),d("div",null,t[0]||(t[0]=[r('<h1 id="effects" tabindex="-1">Effects <a class="header-anchor" href="#effects" aria-label="Permalink to “Effects”">​</a></h1><p>This guide covers utilities that apply visual effects to elements, such as shadows, filters, opacity, and outlines.</p><h2 id="box-shadow" tabindex="-1">Box Shadow <a class="header-anchor" href="#box-shadow" aria-label="Permalink to “Box Shadow”">​</a></h2><p>Add depth and emphasis to elements with box-shadow utilities.</p><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-shadow-sm</code></td><td><code>box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.05);</code></td></tr><tr><td><code>is-shadow-md</code></td><td><code>box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);</code></td></tr><tr><td><code>is-shadow-lg</code></td><td><code>box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);</code></td></tr><tr><td><code>is-shadow-xl</code></td><td><code>box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);</code></td></tr><tr><td><code>is-shadow-2xl</code></td><td><code>box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);</code></td></tr><tr><td><code>is-shadow-inner</code></td><td><code>box-shadow: inset 0 2px 4px 0 rgba(0, 0, 0, 0.06);</code></td></tr><tr><td><code>is-shadow-none</code></td><td><code>box-shadow: none;</code></td></tr></tbody></table><p><strong>Use Case:</strong> Use shadows on cards, modals, and buttons to make them appear elevated from the background.</p><h2 id="opacity" tabindex="-1">Opacity <a class="header-anchor" href="#opacity" aria-label="Permalink to “Opacity”">​</a></h2><p>Control the transparency of elements.</p><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-opacity-0</code></td><td><code>opacity: 0;</code></td></tr><tr><td><code>is-opacity-25</code></td><td><code>opacity: 0.25;</code></td></tr><tr><td><code>is-opacity-50</code></td><td><code>opacity: 0.5;</code></td></tr><tr><td><code>is-opacity-75</code></td><td><code>opacity: 0.75;</code></td></tr><tr><td><code>is-opacity-100</code></td><td><code>opacity: 1;</code></td></tr></tbody></table><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>Arbitrary values are supported: <code>is-opacity-[0.3]</code></p></div><p><strong>Use Case:</strong> Make elements semi-transparent for subtle designs, or combine with transitions for fade-in/fade-out effects.</p><h2 id="filters" tabindex="-1">Filters <a class="header-anchor" href="#filters" aria-label="Permalink to “Filters”">​</a></h2><p>Apply graphical effects like blur, brightness, contrast, and more.</p><h3 id="blur" tabindex="-1">Blur <a class="header-anchor" href="#blur" aria-label="Permalink to “Blur”">​</a></h3><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-blur-sm</code></td><td><code>filter: blur(4px);</code></td></tr><tr><td><code>is-blur-md</code></td><td><code>filter: blur(12px);</code></td></tr><tr><td><code>is-blur-lg</code></td><td><code>filter: blur(16px);</code></td></tr><tr><td><code>is-blur-xl</code></td><td><code>filter: blur(24px);</code></td></tr><tr><td><code>is-blur-2xl</code></td><td><code>filter: blur(40px);</code></td></tr></tbody></table><p><strong>Use Case:</strong> Often used on background images or elements behind a modal to draw focus to the foreground content.</p><h3 id="backdrop-blur" tabindex="-1">Backdrop Blur <a class="header-anchor" href="#backdrop-blur" aria-label="Permalink to “Backdrop Blur”">​</a></h3><p>Apply a blur to the area <em>behind</em> an element.</p><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-backdrop-blur-none</code></td><td><code>backdrop-filter: blur(0);</code></td></tr><tr><td><code>is-backdrop-blur-sm</code></td><td><code>backdrop-filter: blur(4px);</code></td></tr><tr><td><code>is-backdrop-blur-md</code></td><td><code>backdrop-filter: blur(8px);</code></td></tr><tr><td><code>is-backdrop-blur-lg</code></td><td><code>backdrop-filter: blur(12px);</code></td></tr></tbody></table><p><strong>Use Case:</strong> Perfect for creating &quot;glassmorphism&quot; effects on overlays and navigation bars.</p><h3 id="brightness" tabindex="-1">Brightness <a class="header-anchor" href="#brightness" aria-label="Permalink to “Brightness”">​</a></h3><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-brightness-0</code></td><td><code>filter: brightness(0);</code></td></tr><tr><td><code>is-brightness-50</code></td><td><code>filter: brightness(.5);</code></td></tr><tr><td><code>is-brightness-100</code></td><td><code>filter: brightness(1);</code></td></tr></tbody></table><h3 id="contrast" tabindex="-1">Contrast <a class="header-anchor" href="#contrast" aria-label="Permalink to “Contrast”">​</a></h3><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-contrast-0</code></td><td><code>filter: contrast(0);</code></td></tr><tr><td><code>is-contrast-50</code></td><td><code>filter: contrast(.5);</code></td></tr><tr><td><code>is-contrast-100</code></td><td><code>filter: contrast(1);</code></td></tr><tr><td><code>is-contrast-125</code></td><td><code>filter: contrast(1.25);</code></td></tr><tr><td><code>is-contrast-150</code></td><td><code>filter: contrast(1.5);</code></td></tr><tr><td><code>is-contrast-200</code></td><td><code>filter: contrast(2);</code></td></tr></tbody></table><h3 id="other-filters" tabindex="-1">Other Filters <a class="header-anchor" href="#other-filters" aria-label="Permalink to “Other Filters”">​</a></h3><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-grayscale-0</code></td><td><code>filter: grayscale(0);</code></td></tr><tr><td><code>is-grayscale</code></td><td><code>filter: grayscale(100);</code></td></tr><tr><td><code>is-saturate-0</code></td><td><code>filter: saturate(0);</code></td></tr><tr><td><code>is-saturate-50</code></td><td><code>filter: saturate(.5);</code></td></tr><tr><td><code>is-saturate-100</code></td><td><code>filter: saturate(1);</code></td></tr><tr><td><code>is-sepia-0</code></td><td><code>filter: sepia(0);</code></td></tr><tr><td><code>is-sepia</code></td><td><code>filter: sepia(100);</code></td></tr><tr><td><code>is-invert-0</code></td><td><code>filter: invert(0);</code></td></tr><tr><td><code>is-invert</code></td><td><code>filter: invert(100);</code></td></tr></tbody></table><h2 id="outline" tabindex="-1">Outline <a class="header-anchor" href="#outline" aria-label="Permalink to “Outline”">​</a></h2><p>Style the outline of an element, which is often used for accessibility to show focus.</p><table tabindex="0"><thead><tr><th>Class</th><th>CSS</th></tr></thead><tbody><tr><td><code>is-outline-none</code></td><td><code>outline: 2px solid transparent;</code></td></tr><tr><td><code>is-outline-dashed</code></td><td><code>outline: 1px dashed;</code></td></tr><tr><td><code>is-outline-dotted</code></td><td><code>outline: 1px dotted;</code></td></tr><tr><td><code>is-outline-double</code></td><td><code>outline: 4px double;</code></td></tr></tbody></table>',29)]))}const f=e(a,[["render",c]]);export{p as __pageData,f as default};
